import React, {useState, useEffect} from "react"
import { search } from "../BooksAPI";
import Searchoutput from "./comp/Searchoutput";

const  Search =(props) => {
   const [posts, setPosts] = useState([]);
   useEffect( () => { 
      if(props.searchValue !== undefined || posts.length !== 0 ){
         console.log(posts)
      async function fetchData() {
          try {
              const res = await search(props.searchValue)
              setPosts(res);
          } catch (err) {
              console.log(err)
          }
      }
      fetchData();
  
}},[props.searchValue,posts] );


    return (
      <>
      <div className="search-books-input-wrapper">
              <input id="search"
                type="text"N
                placeholder="Search by title, author, or ISB"
              onKeyUp={function(){
                 props.setSearchValue(document.getElementById('search').value);
                
              } }/>
              
            </div>
  <Searchoutput posts = {posts}/>
     
     </>
     );
}
 
export default Search;
